import { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';
import CssBaseline from '@material-ui/core/CssBaseline';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import InfiniteScroll from "react-infinite-scroll-component";


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

function App() {
  const [pokemon, setpokemon] = useState([]);
  const [page, setPage] = useState(1);
  const classes = useStyles();
  const getUsers = async () => {
    const { data } = await axios.get(`https://api.pokemontcg.io/v2/cards?page=${1}&pageSize=10`);
    setpokemon(...pokemon, data.data);
    console.log('....', pokemon.pokemon);
  }



  useEffect(() => {
    getUsers();
  }, []);
  if (pokemon) {
    // console.log('...', pokemon.pokemon);
  }
  return (
    <div className="App">
      <CssBaseline />
      <Container maxWidth="md">
        <div className={classes.root}>
          <Grid container spacing={3}>
            {
              pokemon.map((user) => {
                return (
                  <Grid item xs>
                    <Paper className={classes.paper}>
                      <img src={user.images.small} alt="pokemon" />
                      <div className="heading">
                        <h1 className="header">{user.name}</h1>
                        <h1 className="hp">HP:{user.hp}</h1>
                      </div>
                      <h3 className="attack">Attacks:</h3>
                      <p className="attack">
                        {user.attacks.map((item) => {
                          return item.name;
                        }).join(', ')}
                      </p>

                    </Paper>
                  </Grid>
                )
              })
            }
          </Grid>
        </div>
      </Container>


    </div>
  );
}

export default App;
